<style lang="scss">
	h2 {
		margin-bottom: 1em;
		text-align: left;
	}

	.container {
		padding-bottom: 4em;
	}

	.skill-element {
		width: 70%;
		max-width: 400px;
		margin-left: auto;
		display: flex;
		align-items:center;

		.icon {
			display: inline-block;
			position: relative;
			width: 40px;
			height: 40px;
			margin-right: 1em;

			svg {
				width: 40px !important;
				height: 40px !important;
			}
		}

		.name {
			display: inline-block;
			font-size: 1em;
			vertical-align: center;
			font-weight: 700;
		}
			
		& + .skill-element {
			margin-top: 1.6em;
		}
	}

	.right {
		.skill-element {
			margin-left: 0;
			margin-right: auto;
		}
	}

	@media screen and (max-width: 700px) {
		h2 {
			margin-bottom: .7em;
		}

		.skill-element {
			width: 100%;
			margin-left: auto !important;
			margin-right: auto !important;

			& + .skill-element {
				margin-top: 1.6em;
			}
		}

		.right {
			h2 {
				display: none;
			}

			margin-top: 1.6em;
		}
	}
</style>

<div class="container">
	<div class="left">
		<h2>Skills</h2>

		{#each skills.slice(0, Math.ceil(skills.length / 2)) as skill}
			<div class="skill-element">
				<div class="icon">
					{@html skill.icon}
				</div>
				<div class="name">{skill.name}</div>
			</div>
		{/each}

	</div>
	<div class="right">
		<h2>&nbsp;</h2>

		{#each skills.slice(Math.ceil(skills.length / 2)) as skill}
			<div class="skill-element">
				<div class="icon">
					{@html skill.icon}
				</div>
				<div class="name">{skill.name}</div>
			</div>
		{/each}

	</div>
</div>